mapper.select:
select count(qnano) from qna
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select qnano, userid, subject, content, hit, regtime
from qna
where qnano = #{qnano}

mapper.update:
update qna
set hit = hit + 1
where qnano = #{qnano}

update qna
set subject = #{subject}, content = #{content}
where qnano = #{qnano}
